import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
    const [ingredients, setIngredients] = useState('');
    const [context, setContext] = useState('');
    const [selectedModel, setSelectedModel] = useState('cohere'); // Default to 'cohere'
    const [prompt, setPrompt] = useState('');
    const [response, setResponse] = useState('');
    const [recipes, setRecipes] = useState([]);
    const [error, setError] = useState('');

    const handleQuery = async () => {
        try {
            const result = await axios.post('http://localhost:5002/query', { query: ingredients });
            if (result.data.error) {
                setError(result.data.error);
                setRecipes([]);
                setContext('');
            } else {
                setError('');
                setRecipes(result.data.recipes);
                setContext(result.data.context);
            }
        } catch (error) {
            console.error('Error querying recipes:', error);
            setError('An error occurred while querying recipes.');
        }
    };

    const handleGenerate = async () => {
        try {
            const result = await axios.post('http://localhost:5002/generate', { context, prompt, model: selectedModel });
            if (result.data.error) {
                setError(result.data.error);
                setResponse('');
            } else {
                setError('');
                setResponse(result.data.response);
            }
        } catch (error) {
            console.error('Error generating response:', error);
            setError('An error occurred while generating a response.');
        }
    };

    return (
        <div className="App">
            <h1>Recipe Chatbot</h1>
            <div>
                <input
                    type="text"
                    value={ingredients}
                    onChange={(e) => setIngredients(e.target.value)}
                    placeholder="Enter ingredients"
                />
                <button onClick={handleQuery}>Get Recipes</button>
            </div>
            {error && <div className="error">{error}</div>}
            {context && (
                <div>
                    <h2>Context:</h2>
                    <p>{context}</p>
                </div>
            )}
            {recipes.length > 0 && (
                <div>
                    <h2>Recipes:</h2>
                    {recipes.map((recipe, index) => (
                        <div key={index} className="recipe">
                            <h3>{recipe.RecipeName}</h3>
                            <p>Ingredients: {recipe.Ingredients}</p>
                            <p>Instructions: {recipe.Instructions}</p>
                            {/* Add more details as needed */}
                        </div>
                    ))}
                </div>
            )}
            <div>
                <input
                    type="text"
                    value={prompt}
                    onChange={(e) => setPrompt(e.target.value)}
                    placeholder="Enter your question"
                />
                <select value={selectedModel} onChange={(e) => setSelectedModel(e.target.value)}>
                    <option value="cohere">Cohere</option>
                    <option value="google">Google</option>
                </select>
                <button onClick={handleGenerate}>Ask</button>
            </div>
            {response && (
                <div>
                    <h2>Response:</h2>
                    <p>{response}</p>
                </div>
            )}
        </div>
    );
}

export default App;
