import React, { useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Chatbot.css';
import chef from './images/p4.png'; // Assuming 'chef' image is used in the component

const Chatbot = () => {
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState([]);
  const [showDetails, setShowDetails] = useState({}); // Example state, remove if not needed

  const handleInputChange = (e) => {
    setInput(e.target.value);
  };

  const handleSend = async () => {
    if (input.trim()) {
      const userMessage = { text: input, user: true };
      
      try {
        // Example API call
        const response = await axios.post('http://example.com/api/send-message', { message: input });
        const botResponse = {
          text: response.data.text,
          user: false,
        };
        setMessages([...messages, userMessage, botResponse]);
      } catch (error) {
        console.error('Error sending message:', error);
      }

      setInput('');
    }
  };

  const handleRecipeClick = (msgIndex, recipeIndex) => {
    console.log(`Recipe clicked at message index ${msgIndex}, recipe index ${recipeIndex}`);
  };

  const handleLoadMore = async () => {
    console.log('Load more recipes');
  };

  return (
    <div className="chatbot-container">
      {/* Chatbot UI components */}
    </div>
  );
};

export default Chatbot;
