// src/App.js

import React, { useState } from 'react';
import axios from 'axios';
import RecipeCard from './RecipeCard';
import './App.css';

function App() {
    const [llm, setLlm] = useState('llm1');
    const [ingredients, setIngredients] = useState('');
    const [userInput, setUserInput] = useState('');
    const [response, setResponse] = useState('');
    const [recipes, setRecipes] = useState([]);

    const handleQuery = async () => {
        try {
            const result = await axios.post('http://localhost:3001/api/query', {
                llm,
                query: ingredients
            });
            setRecipes(result.data.recipes || []);
            setResponse(result.data.context || '');
        } catch (error) {
            console.error('Error querying LLM:', error);
        }
    };

    const handleGenerate = async () => {
        try {
            const result = await axios.post('http://localhost:3001/api/generate', {
                llm,
                context: response,
                prompt: userInput
            });
            setResponse(result.data.response);
        } catch (error) {
            console.error('Error generating response:', error);
        }
    };

    return (
        <div className="App">
            <h1>Recipe Finder</h1>
            <div>
                <label>Select LLM: </label>
                <select value={llm} onChange={(e) => setLlm(e.target.value)}>
                    <option value="llm1">LLM 1</option>
                    <option value="llm2">LLM 2</option>
                </select>
            </div>
            <div>
                <label>Enter ingredients: </label>
                <input
                    type="text"
                    value={ingredients}
                    onChange={(e) => setIngredients(e.target.value)}
                />
                <button onClick={handleQuery}>Get Recipes</button>
            </div>
            <div className="recipes">
                {recipes.map((recipe, index) => (
                    <RecipeCard key={index} recipe={recipe} />
                ))}
            </div>
            <div>
                <label>Ask a question: </label>
                <input
                    type="text"
                    value={userInput}
                    onChange={(e) => setUserInput(e.target.value)}
                />
                <button onClick={handleGenerate}>Generate Response</button>
            </div>
            <div className="response">
                <h2>Response:</h2>
                <p>{response}</p>
            </div>
        </div>
    );
}

export default App;
