import React, { useState } from 'react';
import axios from 'axios'; // Import Axios for HTTP requests
import 'bootstrap/dist/css/bootstrap.min.css';
import './Chatbot.css';
import chef from './images/p4.png'; // Import your chef image

const Chatbot = () => {
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState([]);
  const [showDetails, setShowDetails] = useState({});

  // Function to handle input change
  const handleInputChange = (e) => {
    setInput(e.target.value);
  };

  // Function to handle sending user message
  const handleSend = async () => {
    if (input.trim()) {
      const userMessage = { text: input, user: true };

      // Send user input to Flask backend (/chat endpoint)
      try {
        const response = await axios.post("https://35be-34-16-159-99.ngrok-free.app", {
          user_input: input,
          context: messages.map(msg => msg.text).join('\n') // Pass previous messages as context
        });
        
        const botResponse = { text: response.data.response, user: false };
        setMessages([...messages, userMessage, botResponse]);
      } catch (error) {
        console.error('Error sending message:', error);
      }

      setInput(''); // Clear input after sending
    }
  };

  // Function to handle click on recipe name to show/hide details
  const handleRecipeClick = (msgIndex, recipeIndex) => {
    const key = `${msgIndex}-${recipeIndex}`;
    setShowDetails((prev) => ({ ...prev, [key]: !prev[key] }));
  };

  // Placeholder function for loading more recipes if needed
  const handleLoadMore = async () => {
    // Implement functionality to load more recipes
  };

  return (
    <div className="chatbot-container">
      <header className="chatbot-header">
        <h1 className="display-4">RECIPE REALM</h1>
      </header>
      <div className="chatbot-messages">
        {messages.map((msg, msgIndex) => (
          <div key={msgIndex} className={`message ${msg.user ? 'user' : 'bot'}`}>
            <div className="message-box-container">
              {!msg.user && (
                <div className="chef-image">
                  <img src={chef} alt="Chef" />
                </div>
              )}
              <div className={`message-box ${msg.user ? 'user-box' : 'bot-box'}`}>
                {msg.text}
                {msg.recipes && (
                  <div className="recipes">
                    {msg.recipes.map((recipe, recipeIndex) => (
                      <div key={recipeIndex} className="recipe">
                        <h5 onClick={() => handleRecipeClick(msgIndex, recipeIndex)}>{recipe.name}</h5>
                        {showDetails[`${msgIndex}-${recipeIndex}`] && (
                          <div className="recipe-details">
                            <p>{recipe.details}</p>
                            <img src={recipe.image} alt={recipe.name} />
                          </div>
                        )}
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>
          </div>
        ))}
        {/* Button to load more recipes */}
        {messages.length > 0 && messages[messages.length - 1].recipes && (
          <button className="btn btn-secondary" onClick={handleLoadMore}>Load More</button>
        )}
      </div>
      <div className="chatbot-input">
        <input
          type="text"
          className="form-control"
          placeholder="Type your message..."
          value={input}
          onChange={handleInputChange}
          onKeyPress={(e) => {
            if (e.key === 'Enter') {
              handleSend();
            }
          }}
        />
        <button className="btn btn-primary" onClick={handleSend}>Send</button>
      </div>
    </div>
  );
};

export default Chatbot;
