// react-frontend/src/App.js
import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
    const [ingredients, setIngredients] = useState('');
    const [context, setContext] = useState('');
    const [model, setModel] = useState('cohere'); // default to 'cohere'
    const [prompt, setPrompt] = useState('');
    const [response, setResponse] = useState('');

    const handleQuery = async () => {
        try {
            const result = await axios.post('http://localhost:5000/query', { query: ingredients });
            setContext(result.data.context);
        } catch (error) {
            console.error('Error querying recipes:', error);
        }
    };

    const handleGenerate = async () => {
        try {
            const result = await axios.post('http://localhost:5000/generate', { context, prompt, model });
            setResponse(result.data.response);
        } catch (error) {
            console.error('Error generating response:', error);
        }
    };

    return (
        <div className="App">
            <h1>Recipe Chatbot</h1>
            <div>
                <input
                    type="text"
                    value={ingredients}
                    onChange={(e) => setIngredients(e.target.value)}
                    placeholder="Enter ingredients"
                />
                <button onClick={handleQuery}>Get Recipes</button>
            </div>
            {context && (
                <div>
                    <h2>Context:</h2>
                    <p>{context}</p>
                </div>
            )}
            <div>
                <input
                    type="text"
                    value={prompt}
                    onChange={(e) => setPrompt(e.target.value)}
                    placeholder="Enter your question"
                />
                <select value={model} onChange={(e) => setModel(e.target.value)}>
                    <option value="cohere">Cohere</option>
                    <option value="google">Google</option>
                </select>
                <button onClick={handleGenerate}>Ask</button>
            </div>
            {response && (
                <div>
                    <h2>Response:</h2>
                    <p>{response}</p>
                </div>
            )}
        </div>
    );
}

export default App;
