import React, { useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Form, Button, Card, ListGroup, Spinner } from 'react-bootstrap';

function App() {
    const [query, setQuery] = useState('');
    const [recipes, setRecipes] = useState([]);
    const [context, setContext] = useState('');
    const [prompt, setPrompt] = useState('');
    const [response, setResponse] = useState('');
    const [loading, setLoading] = useState(false);

    const handleQuerySubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        try {
            const result = await axios.post('http://localhost:3001/query', { query });
            setRecipes(result.data.recipes);
            setContext(result.data.context);
            setResponse('');
        } catch (error) {
            console.error(error);
        } finally {
            setLoading(false);
        }
    };

    const handleGenerateSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        try {
            const result = await axios.post('http://localhost:3001/generate', { context, prompt });
            setResponse(result.data.response);
        } catch (error) {
            console.error(error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <Container>
            <h1 className="mt-5">Recipe Finder</h1>
            <Form onSubmit={handleQuerySubmit}>
                <Form.Group controlId="query">
                    <Form.Label>Enter your query:</Form.Label>
                    <Form.Control
                        type="text"
                        value={query}
                        onChange={(e) => setQuery(e.target.value)}
                        placeholder="Search for recipes..."
                    />
                </Form.Group>
                <Button variant="primary" type="submit" className="mt-3">
                    {loading ? <Spinner animation="border" size="sm" /> : 'Search'}
                </Button>
            </Form>

            {recipes.length > 0 && (
                <div className="mt-5">
                    <h2>Recipes</h2>
                    {recipes.map((recipe, index) => (
                        <Card key={index} className="mb-3">
                            <Card.Header>{recipe.RecipeName}</Card.Header>
                            <ListGroup variant="flush">
                                <ListGroup.Item><strong>Ingredients:</strong> {recipe.Ingredients}</ListGroup.Item>
                                <ListGroup.Item><strong>Instructions:</strong> {recipe.Instructions}</ListGroup.Item>
                                <ListGroup.Item><strong>Course:</strong> {recipe.Course}</ListGroup.Item>
                                <ListGroup.Item><strong>Diet:</strong> {recipe.Diet}</ListGroup.Item>
                                <ListGroup.Item><strong>Prep Time:</strong> {recipe.PrepTimeInMins} mins</ListGroup.Item>
                                <ListGroup.Item><strong>Cook Time:</strong> {recipe.CookTimeInMins} mins</ListGroup.Item>
                                <ListGroup.Item><strong>Total Time:</strong> {recipe.TotalTimeInMins} mins</ListGroup.Item>
                                <ListGroup.Item><strong>Servings:</strong> {recipe.Servings}</ListGroup.Item>
                                <ListGroup.Item><strong>URL:</strong> <a href={recipe.URL} target="_blank" rel="noopener noreferrer">Recipe Link</a></ListGroup.Item>
                                {recipe['image-url'] && <ListGroup.Item><img src={recipe['image-url']} alt={recipe.RecipeName} style={{ width: '100%' }} /></ListGroup.Item>}
                            </ListGroup>
                        </Card>
                    ))}
                </div>
            )}

            {context && (
                <Form onSubmit={handleGenerateSubmit} className="mt-5">
                    <Form.Group controlId="prompt">
                        <Form.Label>Ask a question based on the recipes:</Form.Label>
                        <Form.Control
                            type="text"
                            value={prompt}
                            onChange={(e) => setPrompt(e.target.value)}
                            placeholder="Enter your question..."
                        />
                    </Form.Group>
                    <Button variant="primary" type="submit" className="mt-3">
                        {loading ? <Spinner animation="border" size="sm" /> : 'Generate Response'}
                    </Button>
                </Form>
            )}

            {response && (
                <div className="mt-5">
                    <h2>Response</h2>
                    <Card>
                        <Card.Body>
                            <Card.Text>{response}</Card.Text>
                        </Card.Body>
                    </Card>
                </div>
            )}
        </Container>
    );
}

export default App;
